import express, { Router } from 'express';
import bodyParser from 'body-parser';
import { Portifolio, Auth, Upload } from '@/app/controllers';
import User from './app/schemas/User';
import swaggerUi from 'swagger-ui-express';

const swaggerFile = require('./swagger.json');

const app = express();
const port = 3000;

app.use(bodyParser.json());
app.use(bodyParser.urlencoded({ extended: false }));

app.use('/portifolio', Portifolio);
app.use('/auth', Auth);
app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerFile));

function createAdminUser() {
    const name = 'Guilherme';
    const email = 'guilherme.rosa@compjr.com';
    const password = 'botw';
    const administrador = true;

    User.findOne({ email })
        .then((userData) => {
            if (userData) {
                console.log('Usurio administrador j existe.');
                return;
            }

            const novoUsuario = new User({
                name,
                email,
                password,
                administrador,
            });
            novoUsuario
                .save()
                .then(() => {
                    console.log('Usurio admin criado com sucesso');
                })
                .catch((error) => {
                    console.error('Erro ao criar usurio:', error);
                });
        })
        .catch((error) => {
            console.error('Erro ao consultar usurio no banco de dados:', error);
        });
}
createAdminUser();

console.log(`Servidor rodando no link https://localhost:${port}`);
app.listen(port);

